import React from 'react';
import './App.scss';
import { FetchCharacters } from './services/FetchCharacters';
import { Switch, Route } from 'react-router-dom';
import Home from './components/Home';
import CharacterCard from './components/CharacterCard';
import HarryPotterSoundtrack from './audio/HarryPotterSoundtrack.mp3';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      characters: [],
      filterName: '',
      favCharacter: [],
      filterHouse: '',
      filterAlive: null
    };

    this.handleFilterName = this.handleFilterName.bind(this);
    this.handleFav = this.handleFav.bind(this);
    this.resetFilters = this.resetFilters.bind(this);
    this.handleFilterHouse = this.handleFilterHouse.bind(this);
    this.handleFilterLive = this.handleFilterLive.bind(this);
  }

  componentDidMount() {
    this.getCharacters();
  }

  getCharacters() {
    FetchCharacters().then(data => {
      const newData = data.map((item, index) => {
        const house = item.house === '' ? 'Without House' : item.house;
        return { ...item, id: index, house: house, alive: alive };
      });

      this.setState({
        characters: newData
      });
    });
  }

  handleFilterName(event) {
    const characterName = event.currentTarget.value;

    this.setState({
      filterName: characterName
    });
  }

  handleFilterHouse(event) {
    const characterHouse = event.currentTarget.value;

    this.setState({
      filterHouse: characterHouse
    });
  }

  handleFilterLive(event) {
    const characterAlive = event.currentTarget.value;

    this.setState({
      filterAlive: characterAlive
    });
  }

  handleFav(event, obj) {
    const id = parseInt(event.currentTarget.id);
    const newFav = [...this.state.favCharacter];
    let index = newFav.indexOf(obj);
    if (this.state.favCharacter.includes(id) === true) {
      newFav.splice(index, 1);
    } else {
      newFav.push(id);
    }
    this.setState({
      favCharacter: newFav
    });
  }

  resetFilters() {
    this.setState({
      filterName: '',
      filterHouse: ''
    });
  }

  render() {
    const {
      characters,
      filterName,
      favCharacter,
      filterHouse,
      filterAlive
    } = this.state;
    return (
      <div className="app">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <Home
                handleFilterName={this.handleFilterName}
                characters={characters}
                filterName={filterName}
                favCharacter={favCharacter}
                handleFav={this.handleFav}
                filterHouse={filterHouse}
                handleFilterHouse={this.handleFilterHouse}
                filterAlive={filterAlive}
                handleFilterLive={this.handleFilterLive}
              />
            )}
          />
          <Route
            path="/detail/:character"
            render={takeParams => (
              <CharacterCard
                takeParams={takeParams}
                characters={characters}
                filterName={filterName}
                resetFilters={this.resetFilters}
              />
            )}
          />
        </Switch>
        {/* <audio autoPlay src={HarryPotterSoundtrack} id="pDst9A3sqis" /> */}
      </div>
    );
  }
}

export default App;
