import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

class CharacterList extends React.Component {
  render() {
    const { characters, filterName, favCharacter, handleFav } = this.props;

    return (
      <ul className="character__list">
        {characters
          .filter(item =>
            item.name.toLowerCase().includes(filterName.toLowerCase())
          )
          .map(item => {
            return (
              <Link
                to={`/detail/${item.id}`}
                className="character__card--link"
                key={item.id}
              >
                <li className="character__item">
                  <div className="character__div">
                    <p className="character__title">
                      Have you seen this{' '}
                      {item.gender === 'male' ? 'wizard' : 'witch'}?
                    </p>
                    <div className="character__image--wrapper">
                      <img
                        className="character__image"
                        src={item.image}
                        alt={item.name}
                      />
                    </div>
                    <h2 className="character__name">{item.name}</h2>
                    <h3 className="character__house">
                      {item.house === '' ? '-' : item.house}
                    </h3>
                    <div
                      className={`character__not-fav character__${
                        favCharacter.includes(item.id) ? 'fav' : ''
                      }`}
                      id={item.id}
                      onClick={handleFav}
                    >
                      <i className="fas fa-magic" />
                    </div>
                  </div>
                </li>
              </Link>
            );
          })}
      </ul>
    );
  }
}

CharacterList.propTypes = {
  characters: PropTypes.array,
  filterName: PropTypes.string,
  favCharacter: PropTypes.array,
  handleFav: PropTypes.func
};

export default CharacterList;
