import React from 'react';
import './App.scss';
import { FetchCharacters } from './services/FetchCharacters';
import Filters from './components/Filters';
import { Switch, Route } from 'react-router-dom';
import CharacterCard from './components/CharacterCard';
import CharacterList from './components/CharacterList';
import HarryPotterLogo from './images/HarryPotterLogo.png';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      characters: [],
      filterName: ''
    };

    this.handleFilterName = this.handleFilterName.bind(this);
    this.resetFilters = this.resetFilters.bind(this);
  }

  componentDidMount() {
    this.getCharacters();
  }

  getCharacters() {
    FetchCharacters().then(data => {
      const newData = data.map((item, index) => {
        return { ...item, id: index };
      });

      this.setState({
        characters: newData
      });
    });
  }

  handleFilterName(event) {
    const characterName = event.currentTarget.value;

    this.setState({
      filterName: characterName
    });
  }

  resetFilters() {
    this.setState({
      filterName: ''
    });
  }

  render() {
    const { characters, filterName } = this.state;
    return (
      <div className="App">
        <header className="app__header">
          <div className="header__img--container">
            {/* <img
              className="header__title"
              src={HarryPotterLogo}
              alt="Harry Potter logo"
            /> */}
            <a target="_top" href="https://www.flamingtext.com/">
              <img
                src="https://blog.flamingtext.com/blog/2019/05/24/flamingtext_com_1558719627_649283243.png"
                border="0"
                alt="Logo Design by FlamingText.com"
                title="Logo Design by FlamingText.com"
              />
            </a>
            <br />
            Logo Design by{' '}
            <a href="https://www.flamingtext.com/">FlamingText.com</a>
          </div>
        </header>
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <React.Fragment>
                <Filters handleFilterName={this.handleFilterName} />
                <CharacterList
                  characters={characters}
                  filterName={filterName}
                />
              </React.Fragment>
            )}
          />
          <Route
            path="/detail/:character"
            render={takeParams => (
              <CharacterCard
                takeParams={takeParams}
                characters={characters}
                filterName={filterName}
                resetFilters={this.resetFilters}
              />
            )}
          />
        </Switch>
      </div>
    );
  }
}

export default App;
