import React from 'react';
import './App.scss';
import { FetchCharacters } from './services/FetchCharacters';
import Filters from './components/Filters';
import { Switch, Route } from 'react-router-dom';
import CharacterCard from './components/CharacterCard';
import CharacterList from './components/CharacterList';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      characters: [],
      filterName: ''
    };

    this.handleFilterName = this.handleFilterName.bind(this);
  }

  componentDidMount() {
    this.getCharacters();
  }

  getCharacters() {
    FetchCharacters().then(data => {
      const newData = data.map((item, index) => {
        return { ...item, id: index };
      });

      this.setState({
        characters: newData
      });
    });
  }

  handleFilterName(event) {
    const characterName = event.currentTarget.value;

    this.setState({
      filterName: characterName
    });
  }

  render() {
    const { characters, filterName } = this.state;
    return (
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <CharacterList handleFilterName={this.handleFilterName} />
            )}
          />
          <Route
            path="/detail/:character"
            render={takeParams => (
              <CharacterCard
                characters={characters}
                parametros={takeParams}
                characters={characters}
                filterName={filterName}
              />
            )}
          />
        </Switch>
      </div>
    );
  }
}

<div className="app">
  <Switch>
    <Route
      exact
      path="/"
      render={() => (
        <Home
          pokemons={pokemons}
          handleNameFilter={this.handleNameFilter}
          filterName={filterName}
        />
      )}
    />
    <Route
      path="/detail/:criatura"
      render={recogeParametros => (
        <PokemonDetail
          parametros={recogeParametros}
          pokemons={pokemons}
          resetFilters={this.resetFilters}
        />
      )}
    />
  </Switch>
</div>;

export default App;
