import React from 'react';
import './App.scss';
import { FetchCharacters } from './services/FetchCharacters';
import Filters from './components/Filters';
import { Switch, Route } from 'react-router-dom';
import CharacterCard from './components/CharacterCard';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      characters: [],
      filterName: ''
    };

    this.handleFilterName = this.handleFilterName.bind(this);
  }

  componentDidMount() {
    this.getCharacters();
  }

  getCharacters() {
    FetchCharacters().then(data => {
      const newData = data.map((item, index) => {
        return { ...item, id: index };
      });

      this.setState({
        characters: newData
      });
    });
  }

  handleFilterName(event) {
    const characterName = event.currentTarget.value;

    this.setState({
      filterName: characterName
    });
  }

  // //Esta funcion va en el componente del input
  // paintFilterArray() {
  //   const { characters, filterName } = this.state;
  //   return characters
  //     .filter(item => item.name.includes(filterName))
  //     .map(item => {
  //       return (
  //
  //         <li className="character" key={item.id}>
  //           {item.name}
  //         </li>
  //       );
  //     });
  // }

  render() {
    const { characters, filterName } = this.state;
    return (
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <Home
                pokemons={pokemons}
                handleNameFilter={this.handleNameFilter}
                filterName={filterName}
              />
            )}
          />
          <Route
            path="/detail/:criatura"
            render={recogeParametros => (
              <PokemonDetail
                parametros={recogeParametros}
                pokemons={pokemons}
                resetFilters={this.resetFilters}
              />
            )}
          />
        </Switch>
      </div>
    );
  }
}

export default App;
